// src/App.js
import React, { useState } from 'react';
import { Button } from '@headlessui/react/dist/components/button';

function App() {
  const [role, setRole] = useState('');

  const handleRoleChange = (selectedRole) => {
    setRole(selectedRole);
  };

  const Navbar = () => {
    return (
      <nav className="bg-blue-500 p-4">
        <div className="container mx-auto flex justify-between items-center">
          <div className="text-white font-bold text-xl">Teacher Student App</div>
          <div className="space-x-4">
            {role === 'teacher' ? (
              <span className="text-white">Teacher Navbar</span>
            ) : (
              <span className="text-white">Student Navbar</span>
            )}
          </div>
        </div>
      </nav>
    );
  };

  return (
    <div>
      <Navbar />
      <div className="container mx-auto mt-8">
        <div className="flex justify-center">
          <Button
            onClick={() => handleRoleChange('teacher')}
            className={`px-4 py-2 ${
              role === 'teacher' ? 'bg-blue-600 text-white' : 'bg-blue-300'
            }`}
          >
            I am a Teacher
          </Button>
          <Button
            onClick={() => handleRoleChange('student')}
            className={`px-4 py-2 ${
              role === 'student' ? 'bg-blue-600 text-white' : 'bg-blue-300'
            }`}
          >
            I am a Student
          </Button>
        </div>
      </div>
    </div>
  );
}

export default App;
